import { useSelector } from "react-redux";
import CartItem from "../components/CartItem";
import { NavLink } from "react-router-dom";
import { useEffect, useState } from "react";

const Cart = () => {

  const { cart } = useSelector((state) => state);
  console.log("printing first");
  console.log(cart);
  const [TotalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart])

  return (
    <div>
      {
        cart.length > 0 ?
          (<div className="flex max-w-6xl p-2 mx-auto space-y-10 space-x-5 min-h-[80vh]">

            <div className="w-[70%]">
              {
                cart.map((item, index) => (
                  <CartItem key={item.id} item={item} itemIndex={index} />
                ))
              }
            </div>
          <div className="flex flex-col ">
            <div>

              <div className="text-[35px] mb-5 text-green-600">Your Cart</div>
              <div className="text-[45px]  text-green-600 mb-8">Summary</div>
              <p>
                <span className="text-lg">Total Item: {cart.length}</span>
              </p>

            </div>

            <div>
              <p>Total Price: ${TotalAmount}</p>
              <button>Checkout Now</button>
            </div>

            </div>

          </div>

          ) : (
            <div>
              <p>cart is empty</p>
              <NavLink to="/">
                <button>Shop Now</button>
              </NavLink>
            </div>
          )

      }
    </div>
  );
};

export default Cart;
