import {AiFillDelete} from "react-icons/ai"

const CartItem = ({ item, itemIndex }) => {
  return (
    <div>
      <div>
        <img src={item.image} />
      </div>
      <div>
        <p>{item.title}</p>
        <p>{item.description}</p>
        <div>
          <div>

            <p>{item.price}</p>
          </div>
          <div onClick={removeFromCart}>
            <AiFillDelete/>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
